import React, { useState, useEffect } from 'react';
import { Dialog } from '@headlessui/react';
import gear from '../assets/visual.png';
import Logo from '../assets/Logo.png';
import { Link } from 'react-router-dom';
import Navbar from './Navbar';
import HeroPage from './HeroPage';
import BrandScroll from './BrandScroll';


const Header = () => {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [isDesktop, setIsDesktop] = useState(window.innerWidth > 768);

  const navItems = [
    { label: 'About', href: '#about' },
    { label: 'Features', href: '#features' },
    { label: 'Customers', href: '#customers' },
    { label: 'Updates', href: '#updates' },
    { label: 'Help', href: '#help' },
  ];

  const partners = [
    'Arrow Corp', 'Quantum', 'Echo Valley', 'Celestial', 'PULSE', 'APEX',
    'Arrow Corp', 'Quantum', 'Echo Valley', 'Celestial', 'PULSE', 'APEX'
  ];

  useEffect(() => {
    const handleResize = () => {
      setIsDesktop(window.innerWidth > 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div className="min-h-screen">
      <header className="absolute w-full top-0 z-50 h-[87px]" style={{backgroundColor:'rgba(234, 238, 254, 1)', boxShadow:'none'}}>
        <nav className="flex items-center justify-between px-4 py-4 lg:px-8 h-full" style={{padding:'1.25rem'}}>
          <Link to="/" className="flex items-center">
            <img src={Logo} alt="Logo" className="h-8 w-auto mr-2" />
          </Link>

          {isDesktop && (
          <div className="flex items-center space-x-8" style={{fontWeight:'400',lineHeight:'23px',color:'rgba(0, 0, 0, 0.6)'}}>
            {navItems.map((item) => (
              <a 
                key={item.label} 
                href={item.href}
                className="text-gray-600 hover:text-gray-900"
              >
                {item.label}
              </a>
            ))}
            <Link to="/joyful/login" className="bg-black text-white px-4 py-2 rounded-lg">
              Get for free
            </Link>
          </div>
          )}

          {!isDesktop && (
          <button 
            className="md:hidden"
            onClick={() => setMobileMenuOpen(true)}
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16m-7 6h7" />
            </svg>
          </button>
           )}
        </nav>

        {/* <Navbar /> */}

        {/* <HeroPage/>
        <BrandScroll /> */}
      </header>



      {/* Hero Section */}


      <style jsx>{`
        @keyframes scroll {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }

        .animate-scroll {
          animation: scroll 20s linear infinite;
        }
      `}</style>
    </div>
  );
};

export default Header;
