import React, { useState } from 'react';
import WebsiteFooter from './WebsiteFooter';
import { Link } from 'react-router-dom';

const LoginPage = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create URLSearchParams object for form data
    const formDataToSend = new URLSearchParams();
    formDataToSend.append('username', formData.username);
    formDataToSend.append('password', formData.password);
    formDataToSend.append('login-form-submit', 'login');

    try {
      const response = await fetch('/app/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: formDataToSend,
        redirect: 'follow', // This ensures fetch follows redirects
        credentials: 'include' // This ensures cookies are sent with the request
      });

      // If the response was redirected, the browser will have followed it automatically
      // The response.url will be the final URL after all redirects
      if (response.ok) {
        window.location.href = response.url;
      } else {
        console.error('Login failed:', response.statusText);
      }
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  return (
    <div className="login-page-container">

      <div className="w-full bg-black text-white" style={{ height: '327px' }}>
        <div className="container mx-auto px-4 py-8 h-full flex flex-col">
          {/* Logo and tagline section */}
          <div className="mb-8">
            <Link to="/"> {/* Use to prop instead of href */}
              <div className="flex items-center">
                 <img src={Logo} alt="Logo" width={30} height={30} className="mr-2" />
                <span className="text-sm md:text-base">
                  Effortlessly turn your ideas into a fully functional, responsive, no-code SaaS website.
                </span>
              </div>
            </Link>
          </div>

          {/* Footer links section */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-8 flex-grow">
            {Object.entries(footerLinks).map(([category, links]) => (
              <div key={category}>
                <h3 className="text-lg font-semibold capitalize mb-4">{category}</h3>
                <ul>
                  {links.map((link) => (
                    <li key={link} className="mb-2">
                      <Link to="#" className="text-gray-400 hover:text-white transition"> {/* Use to prop instead of href */}
                        {link}
                      </Link>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
