// src/App.js
import React from 'react';
import Header from './components/Header';
import LoginForm from './components/LoginForm.tsx';
import HomePage from './components/HomePage';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom'; // Import useLocation
import './App.css';
import WebsiteFooter from './components/WebsiteFooter';

function App() {
  return (
    <Router>
      <div className="App">
        <AppContent /> {/* Render AppContent, which handles conditional rendering of Header */}
      </div>
    </Router>
  );
}

function AppContent() {
  const location = useLocation(); // Get the current location
  const isLoginPage = location.pathname === '/login'; // Check if the current path is /login

  return (
    <>
      {!isLoginPage && <Header />} {/* Render Header only if it's NOT the login page */}
      <main>
        <Routes>
          <Route path='/' element={<HomePage />} />
          <Route path="/login" element={<LoginForm />} />
        </Routes>
      </main>
      {!isLoginPage && <WebsiteFooter />}
    </>
  );
}

export default App;
