import React, { useState } from 'react';
import Navbar from './Navbar';
import Footer from './Footer';
import ArrowIcon from '../assets/signin/arrow.svg';

const LoginPage = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });

  const footerLinks = {
    product: ['Features', 'Integrations', 'Updates', 'FAQ', 'Pricing'],
    company: ['About', 'Blog', 'Careers', 'Manifesto', 'Press', 'Contact'],
    resources: ['Examples', 'Community', 'Guides', 'Docs'],
    legal: ['Privacy', 'Terms', 'Security']
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create URLSearchParams object for form data
    const formDataToSend = new URLSearchParams();
    formDataToSend.append('username', formData.username);
    formDataToSend.append('password', formData.password);
    formDataToSend.append('login-form-submit', 'login');

    try {
      const response = await fetch('/app/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: formDataToSend,
        redirect: 'follow', // This ensures fetch follows redirects
        credentials: 'include' // This ensures cookies are sent with the request
      });

      // If the response was redirected, the browser will have followed it automatically
      // The response.url will be the final URL after all redirects
      if (response.ok) {
        window.location.href = response.url;
      } else {
        console.error('Login failed:', response.statusText);
      }
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  return (
    <div className="min-h-screen flex flex-col">
      {/* Updates section */}
      <div className="w-full bg-indigo-900 text-white flex flex-row justify-center items-center py-3 gap-4">
        <span className='text-sm'>Watch out latest Product Updates Webinar</span>
        <div className='flex flex-row font-semibold items-center gap-2'>
          <span>Joyful CX Feb 2025</span>
          <img src={ArrowIcon} alt="arrow" className="w-4 h-4" />
        </div>
      </div>
      <Navbar></Navbar>

      <div className="flex-1 flex items-center justify-center bg-neutral-100">
        <div className="bg-white border-[0.5px] border-black rounded px-4 py-2">
          <h1>Welcome ðŸ‘‹</h1>
          <h3>Enter your details below</h3>

          <h2>Email</h2>
          <input></input>

          <h2>Password</h2>
          <input></input>
          <p>Forgot password?</p>

          <button>
            <span>Login</span>
            <img src={ArrowIcon} alt="arrow" className="w-4 h-4" />
          </button>
          <h2>Or</h2>
          <button>Sign in with SSO</button>
        </div>
      </div>

      <Footer></Footer>
    </div>
  );
};

export default LoginPage;
