import React, { useState, useEffect } from 'react';

const Engagement = () => {
  const [data, setData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:1337/api/engagement-page');
        const result = await response.json();
        setData(result.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  if (isLoading || !data) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-pulse text-lg font-medium">Loading...</div>
      </div>
    );
  }

  return (
    <div className="relative isolate bg-white">
      {/* Background gradient */}
      <div className="absolute inset-x-0 -top-3 -z-10 transform-gpu overflow-hidden px-4 sm:px-36 blur-3xl">
        <div
          className="mx-auto aspect-[1155/678] w-full max-w-[72.1875rem] bg-gradient-to-tr from-[#ff80b5] to-[#9089fc] opacity-30"
          style={{
            clipPath:
              'polygon(74.1% 44.1%, 100% 61.6%, 97.5% 26.9%, 85.5% 0.1%, 80.7% 2%, 72.5% 32.5%, 60.2% 62.4%, 52.4% 68.1%, 47.5% 58.3%, 45.2% 34.5%, 27.5% 76.7%, 0.1% 64.9%, 17.9% 100%, 27.6% 76.8%, 76.1% 97.7%, 74.1% 44.1%)',
          }}
        />
      </div>

      {/* Main content container */}
      <div className="mx-auto max-w-7xl px-4 py-8 sm:px-6 sm:py-12 lg:px-8 lg:py-16">
        {/* Header section */}
        <div className="mx-auto max-w-4xl text-center">
          <p className="text-sm text-center mb-4">Everything you need</p>

          <h1 
            className="mt-2 text-3xl sm:text-4xl lg:text-5xl font-semibold tracking-tight text-balance"
            style={{
              background: 'linear-gradient(to bottom, rgba(0,0,0,1), rgba(0,19,84,1))',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              minHeight: '3em',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            Streamlined for easy<br />management
          </h1>

          <p className="mx-auto mt-4 max-w-2xl text-sm sm:text-base lg:text-lg text-pretty text-gray-600">
            Enjoy customizable lists, team work tools, and smart tracking all in one place. Set tasks, get reminders, and see your progress simply and quickly.
          </p>
        </div>

        {/* Feature cards section */}
        <div className="mt-8 sm:mt-12 lg:mt-16">
          <div className="mx-auto max-w-4xl">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8 place-items-center">
              {/* Integration ecosystem card */}
              <div className="flex flex-col items-center max-w-sm p-4">
                <div className="relative w-full aspect-square max-w-[280px] sm:max-w-[329px]">
                  <img
                    src="/cube1.png"
                    alt="Integration ecosystem"
                    className="w-full h-full object-contain"
                    loading="lazy"
                  />
                </div>
                <h3 className="mt-4 sm:mt-6 text-lg sm:text-xl font-semibold text-gray-900 text-center">
                  Integration ecosystem
                </h3>
                <p className="mt-2 text-sm sm:text-base text-gray-600 text-center">
                  Enhance your productivity by connecting with your favorite tools, keeping all your essentials in one place.
                </p>
              </div>

              {/* Goal setting card */}
              <div className="flex flex-col items-center max-w-sm p-4">
                <div className="relative w-full aspect-square max-w-[280px] sm:max-w-[329px]">
                  <img
                    src="/cube2.png"
                    alt="Goal setting and tracking"
                    className="w-full h-full object-contain"
                    loading="lazy"
                  />
                </div>
                <h3 className="mt-4 sm:mt-6 text-lg sm:text-xl font-semibold text-gray-900 text-center">
                  Goal setting and tracking
                </h3>
                <p className="mt-2 text-sm sm:text-base text-gray-600 text-center">
                  Define and track your goals, breaking down objectives into achievable tasks to keep your targets in sight.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Engagement;
