import React, { useState, useEffect } from 'react';

const Tracker = () => {
  const [data, setData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:1337/api/tracker-bar');
        const result = await response.json();
        setData(result.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  if (isLoading || !data) {
    return <div>Loading...</div>;
  }

  return (
    <div className="py-24 sm:py-32" style={{background: 'linear-gradient(180deg, #FFFFFF 27.96%, #D2DCFF 100%)'}}>
      <div className="mx-auto max-w-7xl px-6 lg:px-8">
        {/* Decorative background */}
        <div aria-hidden="true" className="absolute inset-x-0 -top-3 -z-10 transform-gpu overflow-hidden px-36 blur-3xl">
          <div
            style={{
              clipPath: 'polygon(74.1% 44.1%, 100% 61.6%, 97.5% 26.9%, 85.5% 0.1%, 80.7% 2%, 72.5% 32.5%, 60.2% 62.4%, 52.4% 68.1%, 47.5% 58.3%, 45.2% 34.5%, 27.5% 76.7%, 0.1% 64.9%, 17.9% 100%, 27.6% 76.8%, 76.1% 97.7%, 74.1% 44.1%)',
            }}
            className="mx-auto aspect-[1155/678] w-[72.1875rem] bg-gradient-to-tr from-[#ff80b5] to-[#9089fc] opacity-30"
          />
        </div>

        {/* Header content */}
        <div className="mx-auto max-w-4xl text-center">
          <button
            className="inline-flex items-center justify-center rounded-lg px-4 py-2 text-sm font-semibold text-black border border-black/10 hover:bg-gray-50 transition-colors"
            onClick={() => { window.location.href = '#'; }}
            style={{
              height: '6vh',
              fontSize: '0.8125rem',
              fontFamily: 'Inter',
              borderRadius: '10px',
              marginBottom: '1.5rem'
            }}
          >
            {data.iconText}
          </button>

          <p 
            className="mt-2 text-5xl font-semibold tracking-tight text-balance text-gray-900"
            style={{
              background: 'linear-gradient(to bottom, rgba(0,0,0,1), rgba(0,19,84,1))',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              height: '22vh',
              fontSize: '3.4rem'
            }}
          >
            abcd
          </p>
        </div>

        <p className="mx-auto mt-6 max-w-2xl text-center text-lg font-medium text-pretty text-gray-600 sm:text-xl/8">
          abcd
        </p>

        {/* Main image */}
        <div className="overflow-hidden mb-10">
          <div className="w-full">
            <img
              src="/tracker.png"
              alt="Progress tracker dashboard"
              className="w-full"
              style={{ objectFit: 'none' }}
            />
          </div>
        </div>

        {/* Features grid */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
          {[
            { title: data.trackPoint1, subtext: data.trackPoint1Subtext },
            { title: data.trackPoint2, subtext: data.trackPoint2Subtext },
            { title: data.trackPoint3, subtext: data.trackPoint3Subtext },
            { title: data.trackPoint4, subtext: data.trackPoint4Subtext }
          ].map((feature, index) => (
            <div key={index}>
              <img 
                className="mb-3" 
                src="/leaf.png" 
                alt={`Feature icon ${index + 1}`}
                width="24"
                height="24"
              />
              <h3 
                className="font-medium mb-3"
                style={{ 
                  fontFamily: 'Inter', 
                  color: 'rgba(0, 0, 0, 1)', 
                  fontSize: '18px' 
                }}
              >
                xxxx
              </h3>
              <p 
                className="text-sm text-black mb-3 text-base"
                style={{ fontFamily: 'Inter', fontWeight: '400' }}
              >
                yyyyy
              </p>
              <button className="text-sm text-black">
                Learn more →
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Tracker;
