import React, { useState } from 'react';

const MenuIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <line x1="3" y1="12" x2="21" y2="12"></line>
    <line x1="3" y1="6" x2="21" y2="6"></line>
    <line x1="3" y1="18" x2="21" y2="18"></line>
  </svg>
);

const ProductivityLanding = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const navItems = [
    { label: 'About', href: '#about' },
    { label: 'Features', href: '#features' },
    { label: 'Customers', href: '#customers' },
    { label: 'Updates', href: '#updates' },
    { label: 'Help', href: '#help' }
  ];

  const partners = [
    'Acme Corp',
    'Quantum',
    'Echo Valley',
    'Celestial',
    'PULSE',
    'APEX'
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-blue-500 text-center">
      {/* Header */}

      {/* Main Content */}
      <main className="max-w-6xl mx-auto px-6 py-12">
        <div className="text-sm mb-4">Version 2.0 is here</div>
        <h1 className="text-5xl font-bold mb-6">Pathway to productivity</h1>
        <p className="text-xl mb-8 max-w-3xl mx-auto">
          Celebrate the joy of accomplishment with an app designed to track your progress, 
          motivate your efforts, and celebrate your successes.
        </p>
        <div className="space-x-4">
          <button className="bg-black text-white px-6 py-3 rounded">
            Get for free
          </button>
          <button className="inline-flex items-center text-black">
            Learn more â†’
          </button>
        </div>
      </main>

      {/* Partners */}
      <footer className="max-w-6xl mx-auto px-6 py-12">
        <div className="grid grid-cols-2 md:grid-cols-6 gap-6 text-gray-600 text-sm">
          {partners.map((partner) => (
            <div key={partner} className="text-center">
              {partner}
            </div>
          ))}
        </div>
      </footer>
    </div>
  );
};

export default ProductivityLanding;
