import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import Logo from '../assets/Logo.png'; // Import the image
import visual from '../assets/visual.png'; // Import the image



const MenuIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <line x1="3" y1="12" x2="21" y2="12"></line>
    <line x1="3" y1="6" x2="21" y2="6"></line>
    <line x1="3" y1="18" x2="21" y2="18"></line>
  </svg>
);

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const navItems = [
    { label: 'About', href: '#about' },
    { label: 'Features', href: '#features' },
    { label: 'Customers', href: '#customers' },
    { label: 'Updates', href: '#updates' },
    { label: 'Help', href: '#help' }
  ];

  return (
    <div className="relative"> {/* remove min-h-screen and bg, it's not needed here */}
      {/* Header */}
      <header className="py-4 px-6" style={{backgroundColor:'rgba(234, 238, 254, 1)'}}>
        <div className="flex justify-between items-center max-w-6xl mx-auto"  style={{fontWeight: '400',color: 'rgba(0, 0, 0, 0.6)'}}>
          {/* Logo */}
          <img src={Logo} alt="Logo" className="h-8 w-auto mr-2" /> {/* Display the image */}

          {/* Desktop Nav */}
          <nav className="hidden md:flex items-center space-x-8">
            {navItems.map((item) => (
              <a key={item.label} href={item.href} className="hover:text-gray-600">
                {item.label}
              </a>
            ))}
          </nav>

          {/* Get for free button */}
          {/* Use Link component for navigation */}
          <Link to="/login" className="hidden md:block bg-black text-white px-4 py-2 rounded">
            Get for free
          </Link>

          {/* Mobile menu button */}
          <button 
            className="md:hidden p-2" 
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            <MenuIcon />
          </button>
        </div>

        {/* Mobile menu */}
        {isMenuOpen && (
          <div className="absolute top-full left-0 right-0 bg-white shadow-lg md:hidden">
            {navItems.map((item) => (
              <a
                key={item.label}
                href={item.href}
                className="block py-3 px-6 hover:bg-gray-50"
              >
                {item.label}
              </a>
            ))}
          </div>
        )}
      </header>

      <div className="w-full min-h-[80vh] relative overflow-hidden" style={{
                width: '100vw',
                height: '80vh'
            }}>
                {/* Decorative Elements - Hidden on mobile */}
                <div className="hidden md:block absolute top-2 right-40 z-10">
                    <img
                        src="/cylinder.png"
                        alt=""
                        className="w-22 h-auto transform rotate-180"
                    />
                </div>
                <div className="hidden md:block absolute bottom-20 right-20 z-10">
                    <img
                        src="/halftorus.png"
                        alt=""
                        className="w-32 h-auto"
                    />
                </div>

                {/* Main Content */}
                <div className="flex flex-col md:flex-row w-full h-full items-center justify-center px-4 md:px-8 gap-8 py-8"
                style={{background: 'linear-gradient(201.28deg, #EAEEFE 45.21%, #183EC2 97.31%)'}}>
                    {/* Content Section */}
                    <div className="w-full md:w-5/12 flex flex-col justify-center space-y-8">
                        {/* News Widget Button */}
                        <div>
                            <button
                                className="inline-flex items-center justify-center rounded-lg px-4 py-2 text-sm font-semibold text-black border border-black/10 hover:bg-gray-50 transition-colors"
                                onClick={() => { window.location.href = '#'; }}
                            >
                                Version 2
                            </button>
                        </div>

                        {/* Main Heading */}
                        <h1 className="text-5xl md:text-7xl font-bold leading-tight tracking-tight"
                            style={{
                                background: 'linear-gradient(to bottom, rgba(0,0,0,1), rgba(0,19,84,1))',
                                WebkitBackgroundClip: 'text',
                                WebkitTextFillColor: 'transparent'
                            }}>
                            Pathway to Productivity
                        </h1>

                        {/* Description */}
                        <p className="text-lg md:text-xl text-[rgba(1,13,62,1)] leading-relaxed max-w-xl">
                        Celebrate the joy of accomplishment with an app designed to track your progress, motivate your efforts, and celebrate your successes.
                        </p>

                        {/* CTA Button */}
                        <div>
                            <button
                                className="inline-flex items-center justify-center rounded-lg px-6 py-3 text-base font-semibold text-white bg-black hover:bg-gray-900 transition-colors"
                                onClick={() => router.push('/about')}
                            >
                                book call
                            </button>
                        </div>
                    </div>

                    {/* Main Visual Section */}
                    <div className="w-full md:w-7/12 mt-8 md:mt-0 flex justify-center items-center relative">
                        <img
                            src="{visual}"
                            className="w-full max-w-[90%] md:max-w-[80%] h-auto transform rotation-180 z-0"
                        />
                        img src={Logo} alt="Logo" className="h-8 w-auto mr-2" />
                    </div>
                </div>
      </div>
    </div>
  );
};

export default Header;














