const ProductivityLanding = () => {
    const navigate = useNavigate();
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const navItems = [
      { label: 'About', href: '#about' },
      { label: 'Features', href: '#features' },
      { label: 'Customers', href: '#customers' },
      { label: 'Updates', href: '#updates' },
      { label: 'Help', href: '#help' }
    ];

    const partners = [
      'Acme Corp',
      'Quantum',
      'Echo Valley',
      'Celestial',
      'PULSE',
      'APEX'
    ];

    const handleGetFree = () => {
      navigate('/login');
    };

    return (
      <div className="min-h-screen bg-gradient-to-b from-blue-50 to-blue-500 text-center">
        <header className="py-4 px-6 relative">
          <div className="flex justify-between items-center max-w-6xl mx-auto">
            <div className="text-2xl font-bold">joyful</div>

            <nav className="hidden md:flex items-center space-x-8">
              {navItems.map((item) => (
                <a key={item.label} href={item.href} className="hover:text-gray-600">
                  {item.label}
                </a>
              ))}
            </nav>

            <button 
              className="hidden md:block bg-black text-white px-4 py-2 rounded"
              onClick={handleGetFree}
            >
              Get for free
            </button>

            <button 
              className="md:hidden p-2" 
              onClick={() => setIsMenuOpen(!isMenuOpen)}
            >
              <MenuIcon />
            </button>
          </div>

          {isMenuOpen && (
            <div className="absolute top-full left-0 right-0 bg-white shadow-lg md:hidden">
              {navItems.map((item) => (
                <a
                  key={item.label}
                  href={item.href}
                  className="block py-3 px-6 hover:bg-gray-50"
                >
                  {item.label}
                </a>
              ))}
              <button
                className="w-full text-left py-3 px-6 hover:bg-gray-50"
                onClick={handleGetFree}
              >
                Get for free
              </button>
            </div>
          )}
        </header>

        <main className="max-w-6xl mx-auto px-6 py-12">
          <div className="text-sm mb-4">Version 2.0 is here</div>
          <h1 className="text-5xl font-bold mb-6">Pathway to productivity</h1>
          <p className="text-xl mb-8 max-w-3xl mx-auto">
            Celebrate the joy of accomplishment with an app designed to track your progress, 
            motivate your efforts, and celebrate your successes.
          </p>
          <div className="space-x-4">
            <button 
              className="bg-black text-white px-6 py-3 rounded"
              onClick={handleGetFree}
            >
              Get for free
            </button>
            <button className="inline-flex items-center text-black">
              Learn more →
            </button>
          </div>
        </main>

        <footer className="max-w-6xl mx-auto px-6 py-12">
          <div className="grid grid-cols-2 md:grid-cols-6 gap-6 text-gray-600 text-sm">
            {partners.map((partner) => (
              <div key={partner} className="text-center">
                {partner}
              </div>
            ))}
          </div>
        </footer>
      </div>
    );
  };
