import React, { useState, useEffect } from 'react';
import { Dialog } from '@headlessui/react';
import gear from '../assets/visual.png';
import Logo from '../assets/Logo.png';
import { Link } from 'react-router-dom';
import Navbar from './Navbar';
import HeroPage from './HeroPage';
import BrandScroll from './BrandScroll';


const Header = () => {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [isDesktop, setIsDesktop] = useState(window.innerWidth > 768);

  const navItems = [
    { label: 'About', href: '#about' },
    { label: 'Features', href: '#features' },
    { label: 'Customers', href: '#customers' },
    { label: 'Updates', href: '#updates' },
    { label: 'Help', href: '#help' },
  ];

  const partners = [
    'Arrow Corp', 'Quantum', 'Echo Valley', 'Celestial', 'PULSE', 'APEX',
    'Arrow Corp', 'Quantum', 'Echo Valley', 'Celestial', 'PULSE', 'APEX'
  ];

  useEffect(() => {
    const handleResize = () => {
      setIsDesktop(window.innerWidth > 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div className="min-h-screen">
      <header className="absolute w-full top-0 z-50 h-[87px]" style={{backgroundColor:'rgba(234, 238, 254, 1)', boxShadow:'none'}}>
        <nav className="flex items-center justify-between px-4 py-4 lg:px-8 h-full" style={{padding:'1.25rem',justifyContent}}>
          <Link to="/" className="flex items-center">
            <img src={Logo} alt="Logo" className="h-8 w-auto mr-2" />
          </Link>

          {isDesktop && (
          <div className="flex items-center space-x-8" style={{fontWeight:'400',lineHeight:'23px',color:'rgba(0, 0, 0, 0.6)'}}>
            {navItems.map((item) => (
              <a 
                key={item.label} 
                href={item.href}
                className="text-gray-600 hover:text-gray-900"
              >
                {item.label}
              </a>
            ))}
            <Link to="/joyful/login" className="bg-black text-white px-4 py-2 rounded-lg">
              Get for free
            </Link>
          </div>
          )}

          {!isDesktop && (
          <button 
            className="md:hidden"
            onClick={() => setMobileMenuOpen(true)}
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16m-7 6h7" />
            </svg>
          </button>
           )}
        </nav>

        {/* <Navbar /> */}

        {/* <HeroPage/>
        <BrandScroll /> */}
      </header>



      {/* Hero Section */}
      <main className="relative pt-[87px]">
        <div className="min-h-[calc(100vh-87px)]" style={{background: 'linear-gradient(201.28deg, #EAEEFE 45.21%, #183EC2 97.31%)'}}>
          <div >
            <div className="flex flex-col md:flex-row items-center">
              {/* Content */}
              <div className="w-full md:w-1/2 text-left md" style={{paddingLeft: '1.25rem'}}>
                <p className="text-sm px-4 py-1 rounded-lg border border-black text-black text-center" style={{width: 'fit-content',marginBottom:'1.3rem'}}>Version 2.0 is here</p>
                <div className="text-6xl md:text-6xl font-bold mb-6" style={{
               fontWeight: 700,
               fontSize: '90px',
               lineHeight: '90px',
               letterSpacing: '-6.5%',
               background: 'linear-gradient(180deg, #000000 0%, #001354 100%)',
               WebkitBackgroundClip: 'text', // Important for text gradient
               WebkitTextFillColor: 'transparent' // Important for text gradient
            }}>
              Pathway to productivity
            </div>

                <p className="text-lg md:text-xl mb-8 text-gray-700 max-w-xl">
                  Celebrate the joy of accomplishment with an app designed to track your progress, motivate your efforts, and celebrate your successes.
                </p>
                <div className="flex space-x-4">
                  <Link to="/joyful/login" className="bg-black text-white px-6 py-3 rounded-lg">
                    Get for free
                  </Link>
                </div>
              </div>

              {/* Image */}
              <div className="w-full md:w-1/2 mt-12 md:mt-0">
                <div className="relative mx-auto" style={{ width: '100%', maxWidth: '511px', height: '448px' }}>
                <img
                  src={gear}
                  alt="Productivity visualization"
                  className="absolute top-0 left-0 w-full h-full object-contain"
                />
              </div>
              </div>
            </div>

            {/* Partner Logos Section with Infinite Scroll */}

          </div>

          <div className="h-[132px] bg-white relative overflow-hidden w-full">
              <div className="absolute w-full h-full flex items-center">
                {/* Gradient Overlay for fade effect */}
                <div className="absolute left-0 w-32 h-full bg-gradient-to-r from-white to-transparent z-10"></div>
                <div className="absolute right-0 w-32 h-full bg-gradient-to-l from-white to-transparent z-10"></div>

                {/* Scrolling container */}
                <div className="flex animate-scroll whitespace-nowrap">
                  {partners.map((partner, index) => (
                    <div
                      key={`${partner}-${index}`}
                      className="flex items-center justify-center mx-12 min-w-[120px]"
                    >
                      <img
                        src="/api/placeholder/120/40"
                        alt={partner}
                        className="h-8 w-auto opacity-50"
                      />
                    </div>
                  ))}
                </div>
              </div>
            </div>
        </div>


      </main>

      <style jsx>{`
        @keyframes scroll {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }

        .animate-scroll {
          animation: scroll 20s linear infinite;
        }
      `}</style>
    </div>
  );
};

export default Header;
