import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Dialog } from '@headlessui/react';
import Logo from "../assets/Group.png";

const Navbar = () => {
    const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

    const navItems = [
        { label: 'About', href: '#about' },
        { label: 'Features', href: '#features' },
        { label: 'Customers', href: '#customers' },
        { label: 'Updates', href: '#updates' },
        { label: 'Help', href: '#help' },
    ];

    return (
        <header className="absolute w-full top-0 z-50 h-[87px]" style={{backgroundColor:'rgba(234, 238, 254, 1)', boxShadow:'none'}}>
        <nav className="flex items-center justify-between px-4 py-4 lg:px-8 h-full" style={{padding:'1.25rem'}}>
          <Link to="/" className="flex items-center">
            <img src={Logo} alt="Logo" className="h-8 w-auto mr-2" />
          </Link>

          {/* Desktop Navigation */}
          {isDesktop && (
          <div className="flex items-center space-x-8" style={{fontWeight:'400',lineHeight:'23px',color:'rgba(0, 0, 0, 0.6)'}}>
            {navItems.map((item) => (
              <a 
                key={item.label} 
                href={item.href}
                className="text-gray-600 hover:text-gray-900"
              >
                {item.label}
              </a>
            ))}
            <Link to="/login" className="bg-black text-white px-4 py-2 rounded-lg">
              Get for free
            </Link>
          </div>
          )}
          {/* Mobile Menu Button */}
          {!isDesktop && (
          <button 
            className="md:hidden"
            onClick={() => setMobileMenuOpen(true)}
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16m-7 6h7" />
            </svg>
          </button>
           )}
        </nav>

        {/* Mobile Menu */}
        <Dialog 
          open={mobileMenuOpen} 
          onClose={() => setMobileMenuOpen(false)}
          className="relative z-50 md:hidden"
        >
          <div className="fixed inset-0 bg-black/20" aria-hidden="true" />
          <div className="fixed inset-y-0 right-0 w-full bg-white p-6 sm:max-w-sm">
            <div className="flex justify-between items-center">
              <div className="text-2xl font-bold">joyful</div>
              <button onClick={() => setMobileMenuOpen(false)}>
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            <div className="mt-6">
              {navItems.map((item) => (
                <a
                  key={item.label}
                  href={item.href}
                  className="block py-3 text-gray-600 hover:text-gray-900"
                >
                  {item.label}
                </a>
              ))}
                <Link to="/login" className="block py-3 text-gray-600 hover:text-gray-900">
              Get for free
            </Link>
            </div>
          </div>
        </Dialog>
      </header>
  )
}

export default Navbar
