'use client'
import React, { useState } from 'react';

const LoginForm = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create URLSearchParams object for form data
    const formDataToSend = new URLSearchParams();
    formDataToSend.append('username', formData.username);
    formDataToSend.append('password', formData.password);
    formDataToSend.append('login-form-submit', 'login');

    try {
      const response = await fetch('/app/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: formDataToSend,
        redirect: 'follow', // This ensures fetch follows redirects
        credentials: 'include' // This ensures cookies are sent with the request
      });

      // If the response was redirected, the browser will have followed it automatically
      // The response.url will be the final URL after all redirects
      if (response.ok) {
        window.location.href = response.url;
      } else {
        console.error('Login failed:', response.statusText);
      }
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  return (
    <div className="login-container" style={{ maxWidth: '400px', margin: '50px auto', padding: '20px', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>Login</h2>
      <form id="login-form" className="nobottommargin" onSubmit={handleSubmit}>
        <div className="form-group" style={{ marginBottom: '15px' }}>
          <label htmlFor="username">Email Address</label>
          <input
            type="text"
            id="username"
            name="username"
            placeholder="Business email"
            value={formData.username}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>
        <div className="form-group" style={{ marginBottom: '15px' }}>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>
        <div className="form-group" style={{ marginBottom: '15px' }}>
          <button type="submit" className="button nomargin" id="login-form-submit" name="login-form-submit" value="login">
            Login
          </button>
        </div>
        <div style={{ textAlign: 'right' }}>
          <a href="/forgot-password" className="fright">Forgot Password?</a>
        </div>
      </form>
    </div>
  );
};

export default LoginForm;
